// STRUCTURE

// BODY
body {
    background-color: $white;

    // HEADER
    header {
        display: flex;
        justify-content: space-between;
        align-items: center;

        height: 48px;
        padding: 12px 16px 12px 36px;

        .button-container {
            display: flex;
        }
    }

    // MAIN
    main {
        position: relative;
        max-width: 100vw;
        max-height: calc(100vh - 72px);
        background-color: $dark-white;

        box-shadow: 0px 0px 0px 1px #0e131809 inset;

        #tasks,
        #content {
            position: absolute;
            width: 100vw;
            height: calc(100vh - 72px);
            background-color: $dark-white;
            box-shadow: 0px 0px 0px 1px #0e131809 inset;
            display: flex;
        }

        // TABS FOR NAVIGATING
        .tab {
            position: absolute;
            top: -60px;

            display: flex;
            align-items: center;

            font-weight: 500;
            font-size: 18px;
            padding: 14px 18px 14px 24px;
            border-radius: 12px 12px 0 0;
            box-shadow: 0px 0px 0px 1px #0e131809 inset;

            color: $dark-grey;
            background-color: $white;

            &#tasks-tab {
                left: 135px;
                content: "Tasks";
                &:not(.active) {
                    path {
                        fill: $grey;
                    }
                }
            }

            &#content-tab {
                // left: 247px;
                left: 284px;
                content: "Content";

                &.active {
                    &::before {
                        bottom: 0px;
                        height: 8px;
                    }
                }
            }

            a svg {
                position: relative;
                top: 1px;
                border-radius: $mini-radius;
                margin-left: 12px;
                padding: 8px;
                path {
                    fill: $dark-grey;
                }
            }

            &:hover {
                cursor: pointer;
            }

            &.active {
                color: $black;
                background-color: $dark-white;

                // covering up the drop-shadow with active tabs
                &::before {
                    position: absolute;
                    left: 1.5px;
                    content: "";
                    bottom: 3.5px;
                    height: 4px;
                    width: calc(100% - 3px);
                    background-color: $dark-white;
                    z-index: 3;
                }

                svg:hover,
                svg:active {
                    background-color: $light-grey;
                }
            }
        }
    }
}

