    }

    &::-webkit-scrollbar-thumb {
        background-color: $grey; /* color of the scroll thumb */
        border-radius: $big-radius; /* roundness of the scroll thumb */
        border: 4px solid $light-grey; /* creates padding around scroll thumb */
    }
    
    &::-webkit-scrollbar-thumb:hover {
        background-color: $dark-grey;
    }

    div.group {
        display: flex;
        flex-direction: column;
        margin: 36px;

        &:last-of-type {
            margin-bottom: 96px;
        }

        h3.open-link {
            font-weight: 400;
            font-size: 14px;
            text-transform: uppercase;
            color: $dark-grey;
            align-self: center;
            display: flex;
            align-items: center;
            padding: 8px;
            border-radius: $mini-radius;

            &.link-absent {
                color: $grey;
                path {
                    fill: $grey;
                }
            }

            svg {
                margin-left: 6px;
            }
            &:not(.link-absent):hover {
                cursor: pointer;
                background-color: $light-grey;
  
            }
        }

        div.group-title {
            display: flex;
            justify-content: space-between;

            background-color: $dark-white;
            padding: 12px 0;
            border-radius: $big-radius;

            h3, input {
                font-weight: 500;
                font-size: 16px;
                color: $dark-grey;
            }

            input {
                width: auto;
                background-color: transparent;
                border: none;
                padding: 2px;
                margin-right: 12px;

                &:hover {
                    cursor: text;

                    & ~ svg.edit-group {
                        background-color: $light-grey;
                    }
                }
            }

            div {
                display: flex;
                position: relative;

                svg.first {
                    display: none
                }

                svg {
                    padding: 8px;
                    border-radius: $mini-radius;
                    &.disabled path {
                        fill: $grey

                    }
                    &:not(.disabled):hover {
                        cursor: pointer;
                        background-color: $light-grey;
                    }
                    &.edit-group {
                        margin-right: 6px;

                        &:hover ~ div.tooltip#edit-group {
                            opacity: 1
                        }
                    }

                    &.delete-group {
                        &:not(.disabled):hover ~ div.tooltip#delete-group {
                            opacity: 1
                        }
                    }
                }

                div.tooltip {
                    font-size: 12px;
                    justify-content: center;
                    top: -32px;
                    opacity: 0;
                    width: 104px;
                    background-color: $black;
                    color: $white;
                    text-align: center;
                    border-radius: $mini-radius;
                    padding: 6px 0;
                    position: absolute;
                    z-index: 1;
                    transition-property: opacity;
                    transition-duration: 0.2s;
    
                    &#edit-group {
                        right: 0px;
                    }
                    &#delete-group {
                        right: -37px;
                    }
    
                    &::after {
                        content: "";
                        position: absolute;
                        top: 100%;
                        left: 50%;
                        margin-left: -5px;
                        border-width: 5px;
                        border-style: solid;
                        border-color: $black transparent transparent transparent;
                    }
                }
            }
        }

        div.tiles {
            display: flex;
            flex-wrap: wrap;
            margin: -12px;
            min-height: 90px;

            article.tile {
                min-width: 260px;
                max-width: 280px;
                flex: 1 1 280px;
                margin: 12px;
                padding: 16px;
                height: max-content;

                border-radius: $big-radius;
                background-color: $white;
                
                filter: drop-shadow(0 0 2px #30303018);

                a.edit-content {
                    padding: 6px;
                    position: absolute;
                    top: 12px;
                    right: 16px;
                    opacity: 0.4;
                    
                    
                    &:hover {
                        cursor: pointer;
                        background-color: $dark-white;
                        border-radius: $mini-radius;
                        opacity: 1;
                        
                    }
                }

                svg {
                    display: flex;
                }

                h3 {
                    width: 85%;
                    white-space: nowrap;
                    overflow: hidden;
                    text-overflow: ellipsis;                  
                    font-size: 18px;
                    font-weight: 500;
                    margin: 4px 0 16px 0;
                }

                // code snippets
                pre {
                    font-size: 14px;
                    line-height: 20px;
                    margin-bottom: 12px;
                    code {
                        white-space: pre-wrap;
                        text-overflow: ellipsis;
                        border-radius: $mini-radius;
                        overflow-wrap: break-word;
                        word-wrap: break-word;
                        word-break: break-all;
                        padding: 12px;
                    }
                }

                hr {
                    border: none;
                    border-top: 2px solid $light-grey;
                }

                a.external-link {
                    color: $dark-grey;
                    margin: 12px 0 4px 0;
                    font-size: 14px;
                    display: flex;
                    flex-direction: row-reverse;
                    justify-content: flex-end;
                    align-items: center;
                    overflow-wrap: break-word;
                    word-wrap: break-word;
                    word-break: break-all;

                    svg {
                        padding-right: 6px;
                    }
                }
            }
        }
    }
}

