// FORMS

// modal background
.background {
    display: none;
    background-color: #0e1318af;
    position: fixed;
    top: 0;
    left: 0;
    width: 100vw;
    height: 100vh;
    z-index: 5;
}

// CODE
code {
    font-family:'Courier New', Courier, monospace;
}

// FORM
form {
    z-index: 8;
    display: none;
    position: fixed;
    margin-left: auto;
    margin-right: auto;
    width: 80%;
    left: 50%;
    top: 50%;
    transform: translate(-50%, -50%);
    padding: 36px;
    flex-direction: column;
    background-color: $white;
    border-radius: $big-radius;
    overflow-y: auto;
    max-height: 80vh;
    max-width: 720px;

    &.active {
        display: flex;
    }

    &#add-column-form,
    &#add-group-form {
        top: 50%;
        left: 50%;
        transform: translate(-50%, -50%);
        max-width: 360px;
        input {
            width:auto;
        }
        p {
            font-size: 12px;
            color: $dark-grey;
            margin: 24px 0 12px 0;
        }
        h1.validate-message {
            margin: 18px 0;
        }
        .form-button-container {
            padding-top: 18px;
            margin-right: 0;
            button.primary {
                margin-right: 0;
            }
        }
    }

    section {
        display: grid;
        grid-template-columns: 4fr 3fr 2px;
        column-gap: 24px;

        div:first-child {
            input {
                width: 80%;
            }
            textarea {
                width: 80%;
                border: none;
                padding: 12px;
                resize: vertical;
                filter: drop-shadow(0 0 1px rgba(48, 48, 48, 0.18));
                border-radius: $mini-radius;
                &:hover {
                    filter: drop-shadow(0 0 2px #30303048);
                }
            }
        }
    }
    pre {
        display: flex;
        width: 80%;
    }
    // unformatted code in form
    textarea[name="conceptDescription"] {
        font-family:'Courier New', Courier, monospace;
        max-height: 30%;
        height: 100px;
        color: $white;
        min-width: 100%;
        max-width: 100%;
        font-size: 14px;
        padding: 12px;
        background-color: $black;
        filter: drop-shadow(0 0 1px rgba(48, 48, 48, 0.18));
        white-space: pre-wrap;
        text-overflow: ellipsis;
        border-radius: $mini-radius;
        overflow-wrap: break-word;
        word-wrap: break-word;
        word-break: break-all;
        &:hover {
            filter: drop-shadow(0 0 2px #30303048);
        }

        &::placeholder {
            color: $grey;
        }
    }
    code:empty:before {
        content:attr(data-text);
        color: $grey;
    }

    select {
        appearance: none;
        padding: 12px;
        width: 70%;
        border-radius: $mini-radius;
        border: none;
        filter: drop-shadow(0 0 1px rgba(48, 48, 48, 0.18));
        background-color: $white;
        &:hover {
            filter: drop-shadow(0 0 2px #30303048);
            cursor: pointer;
        }
    }

    .form-button-container {
        display: flex;
        flex-direction: row-reverse;
        padding-top: 36px;
        margin-right: 10px;
        button {
            align-self: flex-end;
        }
    }

    h1 {
        font-size: 24px;
        font-weight: 500;
        &.validate-message {
            font-size: 14px;
            margin-top: 36px;
            font-weight: 400;
            color: $danger;
            text-align: right;
            padding-right: 26px;
        }
    }

    button {
        &#edit-task-delete,
        &#edit-content-delete {
            display: none;
            margin-right: auto;
        }

        &.close-form {
            position: absolute;
            top: 24px;
            right: 24px;
            padding: 12px;
            display: flex;
            justify-content: center;

            background-color: $white;
            border-radius: $mini-radius;

            &:hover {
                background-color: $light-grey;
                cursor: pointer;
            }
        }
    }

    &.update {
        button {
            &#edit-task-delete,
            &#edit-content-delete {
                display: flex;
            }
        }
    }

    input {
        border: none;
        filter: drop-shadow(0 0 1px rgba(48, 48, 48, 0.18));
        border-radius: $mini-radius;
        padding: 12px;
        font-size: 14px;
        background-color: $white;
        width: 60%;

        &:required, &:required:hover, &:required:focus {
            filter: drop-shadow(0 0 2px $danger);
        }

        &:hover {
            filter: drop-shadow(0 0 2px #30303048);
        }
        &:focus {
            filter: drop-shadow(0 0 2px #30303048);
        }

        &[name="taskEstimatedTimeHr"],
        &[name="taskEstimatedTimeMin"] {
            font-size: 14px;
            width: 70px;
        }

        &[type="radio"] {
            display: none;

            ~ span {
                font-size: 14px;
                padding: 12px 18px;
                display: flex;
                border-radius: $mini-radius;
                background-color: $white;
                filter: drop-shadow(0 0 1px rgba(48, 48, 48, 0.18));

                &:hover {
                    filter: drop-shadow(0 0 2px #30303048);
                    cursor: pointer;
                }

                div {
                    width: 12px;
                    height: 12px;
                    border-radius: 12px;

                    margin-right: 8px;
                }
            }
            &:checked ~ span {
                background-color: $black;
                color: $white;
            }
        }
    }

    .radio-container {
        display: flex;
        gap: 12px;
    }

    label {
        display: block;
        font-size: 12px;
        text-transform: uppercase;
        padding-left: 12px;
        margin: 24px 0 4px 0;
        color: $dark-grey;

        &[for="taskEstimatedTimeMin"] {
            position: relative;
            left: -10px;
        }

        &[for="taskEstimatedTimeHr"],
        &[for="taskEstimatedTimeMin"] {
            margin: 0;
            padding: 0;
            display: initial;
            width: 70px;
            span {
                position: relative;
                right: 60px;
            }
        }

        &[for="taskPriorityRating"] {
            display: flex;
            flex-direction: column;
        }

        &.false-radio {
            margin: 0;
            padding: 0;

            &:hover {
                cursor: pointer;
            }

            span {
                background-color: $white;
                color: $dark-grey;
                padding: 14px;
            }
        }

        span.required {
            color: $danger;
        }
    }
}

