// pop-up tabs
.pop-up {
    position: absolute;
    bottom: 17px;
    width: 280px;
    background-color: $white;
    border-radius: $big-radius $big-radius 0 0;
    padding: 16px;
    filter: drop-shadow(0 0 2px #30303018);
    z-index: 3;

    transition: filter 0.2s ease-out;

    &:hover {
        filter: drop-shadow(0 0 4px #30303028);
    }

    nav {
        display: flex;
        align-items: center;
        margin-bottom: 36px;
        h1 {
            display: none;
            position: absolute;
            left: 60px;
            padding: 12px;
            font-weight: 500;
            font-size: 18px;
            &#peek-status {
                display: none;
                left: auto;
                right: 24px;
                text-transform: capitalize;
                font-size: 16px;
                color: $dark-grey;
            }
            
        }
        img {
            margin-left: 12px;
        }
    }

    button.icon {
        padding: 8px;
        min-width: auto;
        margin-left: auto;
        background-color: $white;
        &:hover {
            background-color: $light-grey;
        }
    }
}

// different states of the pop-up tabs
.pop-up[state=active] {
    &#music {
        nav {
            img {
                display: none;
            }
        }
    }
}

.pop-up#music[state=standby], .pop-up#music[state=peek] {
    nav {
        width: initial;
    }
    #current {
        padding-top: 0;
    }
}

.pop-up[state=standby] {
    height: 36px;
    width: 36px;
    display: flex;

    &:hover {
        cursor: pointer;
    }

    &#time {
        right: 110px;
        nav, nav img {
            display: flex;
            margin-left: 0;
            margin: auto;
        }
        *, nav *:not(img), div ~ h1#peek-time, #stowatch.active, #stopwatch, #pomodoro.active, #pomodoro {
            display: none;
        }
        nav h1 {
            display: none !important
        }
    }

    &#music {
        nav, nav img {
            display: flex;
            margin-left: 0;
            margin: auto;
        }
        *, nav *:not(img), #current-controls {
            display: none
        }
    }
}

.pop-up[state=peek] {
    height: 36px;

    &:hover {
        cursor: pointer;
    }

    &#time {
        nav {
            .collapse {
                visibility: hidden;
            }
            #peek-status {
                display: flex;
            }
        }
        #stopwatch, #pomodoro {
            display: none;
        }
    }

    &#music {
        nav {
            display: flex;
            #current-controls {
                width: 100%;
                .metadata {
                    width: 50%;
                    h1.title {
                        width: 100%;
                        display: flex;
                        position: relative;
                        padding: 0;
                        left: 0;
                    }
                }
                .controls {
                    position: absolute;
                    right: 18px;
                }
            }
    
            img{
                display: none;
            }
            .collapse {
                display: none;
            }
        }
    
        #current, h2, .playlist {
            display: none;
        }
    }
}

// this is the animation for the pomodoro-timer
@keyframes load-spans {
    from {width: 0}
    to {width: inherit}
}

